import randomdef generate_quiz(vocab_list, num_questions=5):    """    Generate a list of multiple-choice quiz questions from vocab pairs.    Each vocab item is expected in the format: "Word: Meaning"    """    vocab_pairs = []    for item in vocab_list:        if ":" in item:            term, meaning = item.split(":", 1)            vocab_pairs.append((term.strip(), meaning.strip()))    if len(vocab_pairs) < 4:        return []  # Not enough data to generate meaningful quiz    questions = []    for _ in range(min(num_questions, len(vocab_pairs))):        term, correct = random.choice(vocab_pairs)        # Get wrong choices (exclude correct answer)        wrong_choices = random.sample(            [meaning for _, meaning in vocab_pairs if meaning != correct],            k=min(3, len(vocab_pairs) - 1)        )        # Mix correct + wrong        options = wrong_choices + [correct]        random.shuffle(options)        questions.append({            "question": f"What is the meaning of '{term}'?",            "options": options,            "answer": correct        })    return questions