import randomdef generate_quiz(vocab_list, num_questions=5, get_image=None):    """    vocab_list like ["Apple: Manzana"]    Always fetches image using the English term.    """    vocab_pairs = [        tuple(map(str.strip, item.split(":", 1)))        for item in vocab_list if ":" in item    ]    if len(vocab_pairs) < 4:        raise ValueError("Need at least 4 vocab pairs to generate a quiz.")    questions = []    for english, translated in random.sample(vocab_pairs, min(num_questions, len(vocab_pairs))):        distractors = [t for _, t in vocab_pairs if t != translated]        wrong_choices = random.sample(distractors, k=min(3, len(distractors)))        options = wrong_choices + [translated]        random.shuffle(options)        image = get_image(english) if get_image else None        questions.append({            "question": f"What is the meaning of '{english}'?",            "options": options,            "answer": translated,            "image": image        })    return questions