import osfrom openai import OpenAIfrom dotenv import load_dotenvload_dotenv()client = OpenAI(api_key=os.getenv("OPENAI_API_KEY"))def generate_rhymes(theme="food", language="Spanish", count=5):    prompt = f"""You are a creative language tutor. For the theme '{theme}', generate a list of rhyming vocabulary items for beginner learners.Each item should include:1. The word in {language}2. The English translation3. A short, fun rhyme written in {language}, using the {language} word and the theme context.Example:- {language}: Pomme    English: Apple    Rhyme: Pomme est un fruit d√©licieux, j'en mange toujours un ou deux.Output format:- {language}: [Translated word]  English: [English word]  Rhyme: [in {language}]Generate {count} such entries based on the theme '{theme}'."""    try:        response = client.chat.completions.create(            model="gpt-3.5-turbo",            messages=[{"role": "user", "content": prompt}],            temperature=0.8        )        content = response.choices[0].message.content        print("üì¶ RHYME RAW:\n", content)        rhymes = []        current = {}        for line in content.split("\n"):            line = line.strip()            if not line:                continue            if line.startswith(f"- {language}:"):                if current:                    rhymes.append(current)                current = {                    "foreign": line.split(":", 1)[1].strip()                }            elif line.startswith("English:"):                if current:                    current["english"] = line.split(":", 1)[1].strip()            elif line.startswith("Rhyme:"):                if current:                    current["rhyme"] = line.split(":", 1)[1].strip()        if current:            rhymes.append(current)        return rhymes    except Exception as e:        print("‚ùå Error generating rhymes:", e)        return []