import osimport requestsfrom dotenv import load_dotenvload_dotenv()UNSPLASH_ACCESS_KEY = os.getenv("UNSPLASH_ACCESS_KEY")UNSPLASH_URL = "https://api.unsplash.com/search/photos"def fetch_image_url(query, fallback_url=None):    """    Fetches the first image URL from Unsplash based on the query.    Optionally provides a fallback URL if nothing is found.    """    if not UNSPLASH_ACCESS_KEY:        raise ValueError("Missing UNSPLASH_ACCESS_KEY in environment.")    try:        response = requests.get(            UNSPLASH_URL,            params={                "query": query,                "per_page": 1,                "orientation": "squarish",                "client_id": UNSPLASH_ACCESS_KEY,            },            timeout=10,        )        response.raise_for_status()        data = response.json()        if data["results"]:            return data["results"][0]["urls"]["small"]        else:            return fallback_url or "https://via.placeholder.com/200"    except Exception as e:        print(f"‚ùå Error fetching image from Unsplash: {e}")        return fallback_url or "https://via.placeholder.com/200"